package graph.theory.learning.tool.data;

public class ButtonEnable {

  private boolean isVertexEnable = false;
  private boolean isEdgeEnable = false;
  private boolean isRmvVertexEnable = false;
  private boolean isRmvEdgeBtn =  false;

  public ButtonEnable() {
    super();
    // TODO Auto-generated constructor stub
  }

  public boolean isVertexEnable() {
    return isVertexEnable;
  }

  public void setVertexEnable(boolean isVertexEnable) {
    this.isVertexEnable = isVertexEnable;
  }

  public boolean isEdgeEnable() {
    return isEdgeEnable;
  }

  public void setEdgeEnable(boolean isEdgeEnable) {
    this.isEdgeEnable = isEdgeEnable;
  }

  public boolean isRmvVertexEnable() {
    return isRmvVertexEnable;
  }

  public void setRmvVertexEnable(boolean isRmvVertexEnable) {
    this.isRmvVertexEnable = isRmvVertexEnable;
  }

  public boolean isRmvEdgeBtn() {
    return isRmvEdgeBtn;
  }

  public void setRmvEdgeBtn(boolean isRmvEdgeBtn) {
    this.isRmvEdgeBtn = isRmvEdgeBtn;
  }


}
